// Home.js
import React, { useEffect, useState } from 'react';
import { useInventorysContext, InventorysContextProvider } from '../context/InventoryContext';

// Components
import InventoryDetails from '../components/InventoryDetails';
import InventoryForm from '../components/InventoryForm';
import EditInventoryForm from '../components/EditInventoryForm';

const Home = () => {
  return (
    <InventorysContextProvider> {/* Wrap the content with InventorysContextProvider */}
      <HomeContent />
    </InventorysContextProvider>
  );
};

const HomeContent = () => {
  const { inventorys, dispatch } = useInventorysContext();
  const [editMode, setEditMode] = useState(false); // State to track edit mode
  const [selectedInventory, setSelectedInventory] = useState(null); // State to store selected inventory for editing

  useEffect(() => {
    const fetchInventorys = async () => {
      try {
        const response = await fetch('/api/inventorys');
        if (response.ok) {
          const json = await response.json();
          dispatch({ type: 'SET_INVENTORYS', payload: json });
        }
      } catch (error) {
        console.error('Error fetching inventory:', error);
      }
    };

    fetchInventorys();
  }, [dispatch]);

  // Function to handle edit click
  const handleEditClick = (inventory) => {
    setSelectedInventory(inventory); // Set the selected inventory for editing
    setEditMode(true); // Set edit mode to true
  };

  return (
    <div className="home">
      <div className="workouts">
        {inventorys &&
          inventorys.map((inventory) => (
            <InventoryDetails
              key={inventory._id}
              inventory={inventory}
              onEditClick={() => handleEditClick(inventory)} // Pass a callback to handle edit click
            />
          ))}
      </div>
      {/* Render EditInventoryForm or InventoryForm based on edit mode */}
      {editMode ? (
        <EditInventoryForm
          inventory={selectedInventory}
          setEditMode={setEditMode}
        />
      ) : (
        <InventoryForm />
      )}
    </div>
  );
};

export default Home;
