import React, { useState, useEffect } from 'react';
import { useInventorysContext } from '../hooks/useInventorysContext';

const EditInventoryForm = ({ inventory, setEditMode }) => {
    const { dispatch } = useInventorysContext();

    const [title, setTitle] = useState('');
    const [desc1, setDesc1] = useState('');
    const [desc2, setDesc2] = useState('');

    useEffect(() => {
        if (inventory) {
            setTitle(inventory.title);
            setDesc1(inventory.desc1);
            setDesc2(inventory.desc2);
        }
    }, [inventory]);

    const handleTitleChange = (e) => {
        setTitle(e.target.value);
    };

    const handleDesc1Change = (e) => {
        setDesc1(e.target.value);
    };

    const handleDesc2Change = (e) => {
        setDesc2(e.target.value);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const updatedInventory = { title, desc1, desc2 };

        try {
            const response = await fetch(`/api/inventorys/${inventory._id}`, {
                method: 'PATCH',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(updatedInventory),
            });

            if (response.ok) {
                const updatedInventoryData = await response.json();
                dispatch({ type: 'UPDATE_INVENTORY', payload: updatedInventoryData });
                setEditMode(null); // Exit edit mode by resetting the selected inventory
            } else {
                const errorData = await response.json();
                console.error('Error updating inventory:', errorData.error);
            }
        } catch (error) {
            console.error('Error updating inventory:', error);
        }
    };

    return (
        <form className="inventory-form" onSubmit={handleSubmit}>
            <h3>Edit Inventory</h3>
            <label>Inventory Title</label>
            <input type="text" value={title} onChange={handleTitleChange} />
            <label>Description</label>
            <input type="text" value={desc1} onChange={handleDesc1Change} />
            <label>Additional Information</label>
            <input type="text" value={desc2} onChange={handleDesc2Change} />
            <button type="submit">Update Detail</button>
        </form>
    );
};

export default EditInventoryForm;
