import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Navbar = ({ handleSearch, handleSort }) => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    handleSearch(searchTerm);
  };

  return (
    <header>
      <div className="container">
        <Link to="/">
          <h1>Inventory</h1>
        </Link>
        <form className="search-bar" onSubmit={handleSubmit}>
          <input
            type="text"
            className="search-input"
            placeholder="Search..."
            value={searchTerm}
            onChange={handleChange}
          />
          <button type="submit" className="search-button">Search</button>
        </form>
        <div className="sort-buttons">
          <button onClick={() => handleSort('quantity')} className="sort-button">Sort by Quantity</button>
          <button onClick={() => handleSort('expdate')} className="sort-button">Sort by Expiration Date</button>
        </div>
      </div>
    </header>
  );
};

export default Navbar;
