import React, { useEffect, useState } from 'react';
import { useInventorysContext } from '../hooks/useInventorysContext';
import InventoryDetails from '../components/InventoryDetails';
import InventoryForm from '../components/InventoryForm';

const Home = () => {
  const { inventorys } = useInventorysContext();
  const [filteredInventorys, setFilteredInventorys] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    setFilteredInventorys(inventorys);
  }, [inventorys]);

  const handleSearch = async () => {
    try {
      const response = await fetch(`/api/inventorys/search?searchTerm=${searchTerm}`);
      if (response.ok) {
        const inventoryData = await response.json();
        setFilteredInventorys(inventoryData);
      } else {
        console.error('Error searching inventory:', response.statusText);
      }
    } catch (error) {
      console.error('Error searching inventory:', error);
    }
  };

  const handleSort = (field) => {
    const sortedInventorys = [...filteredInventorys].sort((a, b) => {
      if (field === 'quantity') {
        return a.desc1 - b.desc1;
      } else if (field === 'expdate') {
        return new Date(a.expdate) - new Date(b.expdate);
      } else {
        return 0;
      }
    });
    setFilteredInventorys(sortedInventorys);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    handleSearch();
  };

  return (
    <div className="home">
      <form onSubmit={handleSubmit}>
        <input type="text" value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} />
        <button type="submit">Search</button>
      </form>
      <div className="workouts">
        {filteredInventorys &&
          filteredInventorys.map((inventory) => (
            <InventoryDetails key={inventory._id} inventory={inventory} />
          ))}
      </div>
      <button onClick={() => handleSort('quantity')}>Sort by Quantity</button>
      <button onClick={() => handleSort('expdate')}>Sort by Expiration Date</button>
      <InventoryForm />
    </div>
  );
};

export default Home;
