import { useState } from "react"
import { useInventorysContext } from '../hooks/useInventorysContext'

const InventoryForm = () => {
    const {dispatch}=useInventorysContext()

    const [title, setTitle] = useState('')
    const [desc1, setDesc1] = useState('')
    const [desc2, setDesc2] = useState('')
    const [error, setRError] = useState(null)
    const [emptyFields,setEmptyFields]=useState([])

    const handleSubmit=async(e)=>{
        e.preventDefault()

        const inventory={title, desc1,desc2}

        const response=await fetch('/api/inventorys',{
            method:'POST',
            body:JSON.stringify(inventory),
            headers:{
                'Content-Type':'application/json'
            }
        })
        const json=await response.json()

        if(!response.ok){
            setRError(json.error)
            setEmptyFields(json.emptyFields)
        }
        if(response.ok){
            setTitle('')
            setDesc1('')
            setDesc2('')
            setRError(null)
            setEmptyFields([])
            console.log('New detail added',json)
            dispatch({type:'CREATE_INVENTORYS',payload:json})
        }
    }

    return (
        <form className="create" onSubmit={handleSubmit}>
            <h3>Add an Inventory</h3>

            <label>Inventory Title</label>
            <input type="text"
                onChange={(e) => setTitle(e.target.value)}
                value={title}
                className={emptyFields.includes('title')?'error':''}
            />

            <label>Description</label>
            <input type="text"
                onChange={(e) => setDesc1(e.target.value)}
                value={desc1}
                className={emptyFields.includes('desc1')?'error':''}
            />

            <label>Additional information</label>
            <input type="text"
                onChange={(e) => setDesc2(e.target.value)}
                value={desc2}
                className={emptyFields.includes('desc2')?'error':''}
            />

            <button>Add Detail</button>
            {error && <div className="error">{error}</div>}
        </form>
    )
}

export default InventoryForm
