import { useInventorysContext } from "../hooks/useInventorysContext"

//date fns
//new
import formatDistanceToNow from 'date-fns/formatDistanceToNow'

const InventoryDetails=({inventory,onEditclick})=>{
    const {dispatch}=useInventorysContext()

    const handleEditClick=()=>{
        console.log('Edit button clicked');
    }

    const handleClick=async()=>{

        const response=await fetch('/api/inventorys/'+inventory._id,{
            method:'DELETE'
        })
        const json=await response.json()

        if(response.ok){
            dispatch({type:'DELETE_INVENTORY',payload:json})
        }
    }

    return(
        <div className="workout-details">
            <h4>{inventory.title}</h4>
            <p><strong>Description :</strong>{inventory.desc1}</p>
            <p><strong>Additional Information: </strong>{inventory.desc2}</p>
            <p>{formatDistanceToNow(new Date(inventory.createdAt),{addSuffix:true})}</p>
            <span className="material-symbols-outlined" onClick={handleClick}>delete</span>
            <button onClick={handleEditClick}>Edit</button>
        </div>
    )
}

export default InventoryDetails
