import React from 'react';
import { useInventorysContext } from "../hooks/useInventorysContext";
import formatDistanceToNow from 'date-fns/formatDistanceToNow';

const InventoryDetails = ({ inventory, onEditClick }) => {
    const { dispatch } = useInventorysContext();

    // Function to handle click event for editing an inventory
    const handleEditClick = () => {
        onEditClick(inventory); // Pass the selected inventory to the parent component
    };

    // Function to handle click event for deleting an inventory
    const handleDeleteClick = async () => {
        try {
            const response = await fetch(`/api/inventorys/${inventory._id}`, {
                method: 'DELETE'
            });
            if (response.ok) {
                const deletedInventory = await response.json();
                dispatch({ type: 'DELETE_INVENTORY', payload: deletedInventory });
            } else {
                throw new Error('Failed to delete inventory');
            }
        } catch (error) {
            console.error('Error deleting inventory:', error);
            // Handle error (e.g., display error message)
        }
    };

    return (
        <div className="workout-details">
            <h4>{inventory.title}</h4>
            <p><strong>Description:</strong> {inventory.desc1}</p>
            <p><strong>Additional Information:</strong> {inventory.desc2}</p>
            <p>{formatDistanceToNow(new Date(inventory.createdAt), { addSuffix: true })}</p>
            <span className="material-symbols-outlined" onClick={handleDeleteClick}>Delete</span>
            <button onClick={handleEditClick}>Edit</button> {/* Add an edit button */}
        </div>
    );
};

export default InventoryDetails;
