import React, { useState } from 'react';
import { useInventorysContext } from '../hooks/useInventorysContext';

// Components
import InventoryDetails from '../components/InventoryDetails';
import InventoryForm from '../components/InventoryForm';
import EditInventoryForm from '../components/EditInventoryForm'; // Import the edit form component

const Home = () => {
    const { inventorys } = useInventorysContext(); // Destructure only the needed value

    const [selectedInventory, setSelectedInventory] = useState(null); // State to store the selected inventory for editing

    // Function to handle click event for editing an inventory
    const handleEditClick = (inventory) => {
        setSelectedInventory(inventory); // Set the selected inventory for editing
    };

    return (
        <div className="home">
            <div className="workouts">
                {inventorys && inventorys.map((inventory) => (
                    <InventoryDetails key={inventory._id} inventory={inventory} onEditClick={handleEditClick} />
                ))}
            </div>
            {selectedInventory ? ( // Render the edit form if an inventory is selected for editing
                <EditInventoryForm inventory={selectedInventory} setEditMode={setSelectedInventory} />
            ) : (
                <InventoryForm />
            )}
        </div>
    );
};

export default Home;
